import React, {FC, useEffect, useState} from "react";
import { Character } from "../types/type";

const useLoadingandError = (getApiInfo: any, handleModalMemorized: Function) => {
    const [value, setValue] = useState<null | Character>(null)
    const [loading, setLoading] = useState<boolean>(false)
    const [error, setError] = useState<boolean>(false)
  
    const handlerClickCharacter = (url: string) => {
      return async() => {

            setLoading(true)
        try {
          const res = await getApiInfo()
            setValue(res)
            handleModalMemorized()
        } catch (e) {
          setError(true)
        } finally {
          setLoading(false)
        }
      }
  
  
    }  
    return {value, loading, error, handlerClickCharacter};
}

export default useLoadingandError;
